mapper.select:
SELECT s.id,s.title,b.name bname,s.saleable,s.brand_id,s.cid1,s.cid2,s.cid3,s.sub_title, CONCAT_WS('/',(SELECT c1.name FROM tb_category c1 WHERE c1.id=s.cid1),(SELECT c2.name FROM tb_category c2 WHERE c2.id=s.cid2),(SELECT c3.name FROM tb_category c3 WHERE c3.id=s.cid3)) cname FROM tb_brand b,tb_spu s
s.brand_id=b.id and s.valid=1
and title like '%${key}%'
and s.saleable =#{saleable}
limit #{page},#{rows}
